<style lang="scss">
.oferte {
    &__title {
        font-size: 32px;
        font-weight: 700;
    }

    &__subtitle {
        font-size: 24px;
        font-weight: 50;
        margin-bottom: 20px;
    }

    &__row {
        display: flex;
        flex-wrap: wrap;
        row-gap: 40px;
        margin: 0 -20px;
    }

    &__column {
        flex: 0 1 33.333%;
        padding: 0 20px;

        @media screen and (max-width: 992px) {
            flex: 0 1 50%;
        }

        @media screen and (max-width: 768px) {
            flex: 0 1 100%;
        }
    }

    &__item {
        display: flex;
        align-items: center;
        gap: 10px;
        height: 100%;
        padding: 15px;
        border-radius: 11px;

        transition: all 0.3s linear;

        .icon {
            background-color: rgb(0, 104, 90);
            padding: 15px;
            border-radius: 50%;
            position: relative;
        }

        svg {
            width: 35px;
            height: 35px;
            color: white;
            object-fit: cover;
        }
    }

    &__name {
        font-size: 16px;
        font-weight: 700;
        flex: 1;
        margin-bottom: 5px;
    }
}
</style>
